using Microsoft.AspNetCore.Mvc;

namespace ${ns}.Common.Endpoint;

public enum ${ns}ActionResultStatusType {
	Success = 1 , Error = 2 
}
public class ${ns}ActionResult : IActionResult
{
	public ${ns}ActionResult(${ns}ActionResultStatusType status, Object data, string msg = "")
	{
		Status = status;
		Data = data;
	}
	public ${ns}ActionResultStatusType Status { set; get; }
	public object Data { set; get; }
	public string Message { set; get; } = "";
	public Task ExecuteResultAsync(ActionContext context)
	{
		context.HttpContext.Response.WriteAsJsonAsync(new { @status = Status, @data = Data, @msg = Message });
		return Task.CompletedTask;
	}

	public static ${ns}ActionResult Success(Object data) => new ${ns}ActionResult( ${ns}ActionResultStatusType.Success , data);
	public static ${ns}ActionResult Error(string msg) => new ${ns}ActionResult(${ns}ActionResultStatusType.Error, null, msg);
	public static ${ns}ActionResult Exception(Exception ex) => new ${ns}ActionResult(${ns}ActionResultStatusType.Error, ex, ex.Message);
