using Devinux.AutoDI;
using ${ns}.Common.Application;
using ${ns}.Common.Interfaces;

namespace ${ns}.Common.EndPoint
{
    public interface IQueryBus { 
        Task<ApplicationResult<TResult>> RunService<TQuery, TResult>(TQuery r, CancellationToken c = default) where TQuery : IQuery;
    }
    [DevinuxServiceScope(Devinux.AutoDI.Enums.DevinuxServiceScopeType.Singleton)]
    public class QueryBus : IQueryBus
    {
        private readonly IServiceProvider _sp;
        public QueryBus(IServiceProvider sp)
        {
            _sp = sp;
        }
        public async Task<ApplicationResult<TResult>> RunService<TQuery, TResult>(TQuery r, CancellationToken c = default) where TQuery : IQuery
        {
            var s = _sp.CreateScope().ServiceProvider.GetRequiredService<IQueryHandler<TQuery, TResult>>();
            return await s.Handle(r, c);
        }
    }
    public interface ICommandBus {
        Task<ApplicationResult<TResult>> RunService<TCommand, TResult>(TCommand r, CancellationToken c = default) where TCommand : ICommand;
    }
    [DevinuxServiceScope(Devinux.AutoDI.Enums.DevinuxServiceScopeType.Singleton)]
    public class CommandBus :ICommandBus {
        private readonly IServiceProvider _sp;
        public CommandBus(IServiceProvider sp)
        {
            _sp = sp;
        }
        public async Task<ApplicationResult<TResult>> RunService<TCommand, TResult>(TCommand r, CancellationToken c = default) where TCommand : ICommand
        {
            var s = _sp.CreateScope().ServiceProvider.GetRequiredService<ICommandHandler<TCommand, TResult>>();
            return await s.Handle(r, c);
        }
    }
}


