        public readonly IDevinuxQueryServiceRunner qbus;
        public readonly IMapper mapper;
        public BaseController(IDevinuxCommandServiceRunner cb , IDevinuxQueryServiceRunner qb , IMapper _mapper)
        {
            mapper = _mapper;
            qbus = qb;
            cbus = cb;
        }
    }
}




